        // 1ì„¸íŠ¸ê°€ ëë‚œ ê²½ìš°
        if (countTime.toString() === exercise.time && countTotalTime < exercise.set * exercise.time) {
          // ì„¸íŠ¸ ìˆ˜ ì¦ê°€
