            }, 1000);
            setTimeout(() => { handleClose() }, breakTime*1000)       // ëª¨ë‹¬ close     
            setTimeout(() => { webcam.play() }, breakTime*1000)          
          }
        }    
      }
      status = 'wait';
      
    } else if (prediction[1].probability.toFixed(2) > 0.9) {

        $('.circle-big .progress').css('stroke', '#ff0000');

      status = 'action';
    }
//-----------------------------------------
