import * as tmPose from '@teachablemachine/pose';
import React, { useState, useEffect } from 'react';
import './ExerciseRoom.css';
import $ from 'jquery';
import { useDispatch, useSelector } from 'react-redux';
import { EXERCISE_HISTORY_REGISTER_REQUEST } from '../../store/modules/exerciseHistory';
import dayjs from 'dayjs';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import { Row, Col } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import arrowImage from '../../assets/images/exerciseTutorial/arrow_down.png';

const style = {
  position: 'absolute',
  textAlign: 'center',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 350,
  height: 300,
  bgcolor: 'background.paper',
  boxShadow: 24,
  borderRadius: '20px',
  p: 4,
};

export default function ExerciseRoom() {
  const dispatch = useDispatch();
  const { breakTime } = useParams();
  const { exercise } = useSelector((state) => state.exerciseHistory);
  const now = new Date(); // ÌòÑÏû¨ ÎÇ†Ïßú Î∞è ÏãúÍ∞Ñ

  const ExerciseTypeList = [
    'Î≤§Ìä∏ Ïò§Î≤Ñ Î°úÏö∞',
    'Îç§Î≤®Ïª¨',
    'ÌîÑÎ°†Ìä∏ Î†àÏù¥Ï¶à',
    'Îü∞ÏßÄ',
    // 'Ïò§Î≤Ñ Ìó§Îìú ÌîÑÎ†àÏä§',
    // 'Ìë∏ÏãúÏóÖ',
    'ÏÇ¨Ïù¥Îìú Î†àÌÑ∞Îü¥ Î†àÏù¥Ï¶à',
    'Ïä§ÏøºÌä∏',
    // 'Ïä§ÌÉ†Îî© ÏÇ¨Ïù¥Îìú ÌÅ¨Îü∞Ïπò',
  ];

  const [ URL, setURL ] = useState('');
  const [ currentSet, setCurrentSet ] = useState(1);
  const [ currentCount, setCurrentCount ] = useState(0);
  const [ pageLoading, setPageLoading ] = useState(false);

  // Î™®Îã¨Ï∞Ω Í¥ÄÎ¶¨ 
  const [ open, setOpen ] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const [ timer, setTimer ] = useState(0);

  let modelURL = URL + 'model.json';
  let metadataURL = URL + 'metadata.json';
  let model, webcam, ctx, labelContainer, maxPredictions;

  // Ïö¥ÎèôÎ≥Ñ ÏÑ§Î™Ö Ïù¥ÎØ∏ÏßÄ ÏÑ†ÌÉù
  // '../../assets/images/exerciseTutorial/1/0.png'
  const beforeImageURL = require('../../assets/images/exerciseTutorial/' + exercise.type + '/0.png');
  const afterImageURL = require('../../assets/images/exerciseTutorial/' + exercise.type + '/1.png');

  useEffect(() => {
    switch (exercise.type) {
      case 1: // BentOverRow
        // test : Ïôº ÏÜê Îì§Í∏∞
        // setURL('https://teachablemachine.withgoogle.com/models/tNxgspb7K/');

        setURL('https://teachablemachine.withgoogle.com/models/8htc5Fdlu/');
        break;
      case 2: // DumbbellCurl
        setURL('https://teachablemachine.withgoogle.com/models/qvMroKavg/');
        break;
      case 3: // FrontRaise
        setURL('https://teachablemachine.withgoogle.com/models/aKluCaORU/');
        break;
      case 4: // Lunge
        setURL('https://teachablemachine.withgoogle.com/models/7zoTQArlc/');
        break;
      // case 5: // OverheadPress
      //   setURL('https://teachablemachine.withgoogle.com/models/QoQ4ty5qS/');
      //   break;
      // case 6: // PushUp
      //   setURL('https://teachablemachine.withgoogle.com/models/R6Q1RWNar/');
      //   break;
      case 5: // SideLateralRaise
        setURL('https://teachablemachine.withgoogle.com/models/2GMC5JVkX/');
        break;
      case 6: // Squat
        setURL('https://teachablemachine.withgoogle.com/models/050JkD2Z0/');
        break;
      // case 9: // StandingSideCrunch
      //   setURL('https://teachablemachine.withgoogle.com/models/S9Mpp7iGf/');
      //   break;
    }
  }, []);

  useEffect(() => {
    modelURL = URL + 'model.json';
    metadataURL = URL + 'metadata.json';
    console.log(modelURL, metadataURL);
    if (URL !== '') {
      init();
    }
  }, [URL]);


  async function init() {
    // const modelURL = URL + 'model.json';
    // const metadataURL = URL + 'metadata.json';
    // console.log(modelURL);
    // console.log(metadataURL);
    // load the model and metadata
    // Refer to tmImage.loadFromFiles() in the API to support files from a file picker
    // Note: the pose library adds a tmPose object to your window (window.tmPose)
    model = await tmPose.load(modelURL, metadataURL);
    maxPredictions = model.getTotalClasses();
    // Convenience function to setup a webcam
    const size = 500;
    const flip = true; // whether to flip the webcam
    var loading = false;
    webcam = new tmPose.Webcam(size, size, flip); // width, height, flip
    await webcam.setup(); // request access to the webcam
    console.log(webcam);
    await webcam.play();

    // ÌôîÎ©¥Ïù¥ Î°úÎî©ÎêòÏóàÎã§Î©¥ ÏïÑÏù¥ÏΩò Ï†úÍ±∞
    loading = true;
    setPageLoading(loading);

    window.requestAnimationFrame(loop);
    // append/get elements to the DOM
    const canvas = document.getElementById('canvas');
    canvas.width = size;
    canvas.height = size;
    ctx = canvas.getContext('2d');
    // labelContainer = document.getElementById('label-container');
    // for (let i = 0; i < maxPredictions; i++) {
    //   // and class labels
    //   labelContainer.appendChild(document.createElement('div'));
    // }
  }

  // async function stopVideo() {
  // }

  async function loop(timestamp) {
    webcam.update(); // update the webcam frame
    await predict();
    window.requestAnimationFrame(loop);
  }

  var angle = Math.floor((360 / exercise.time) * 10) / 10;
  var progress = 360;
  var status = 'wait';
  var countTime = 0;
  var countTotalTime = 0;
  var countSet = 1;
  var soundURL = '';

  async function predict() {
    // Prediction #1: run input through posenet
    // estimatePose can take in an image, video or canvas html element
    const { pose, posenetOutput } = await model.estimatePose(webcam.canvas);
    // Prediction 2: run input through teachable machine classification model
    const prediction = await model.predict(posenetOutput);

    if (prediction[0].probability.toFixed(2) > 0.9) {
      if (status == 'action') {
        countTime++;
        countTotalTime++;
        setCurrentCount(countTotalTime);

        // Ïπ¥Ïö¥ÌåÖ ÏÜåÎ¶¨ Ïû¨ÏÉù
        soundURL = require('../../assets/soundEffects/countSound/ddiring2.mp3');
        var audio = new Audio(soundURL);
        audio.volume = 0.8;
        audio.play();

        console.log(status, countTime, soundURL);
        progress = progress - angle;
        if (progress < 0) {
          progress = 360 - angle;
        }
        $('.progress').css('stroke-dashoffset', progress);
        $('#counter').html(countTime);
        console.log(progress, angle);

        if (countTotalTime === exercise.set * exercise.time) {
          handleExercise(countTotalTime);
        }

        // 1ÏÑ∏Ìä∏Í∞Ä ÎÅùÎÇú Í≤ΩÏö∞
        if (countTime.toString() === exercise.time && countTotalTime < exercise.set * exercise.time) {
          // ÏÑ∏Ìä∏ Ïàò Ï¶ùÍ∞Ä
          countSet++;
          setCurrentSet(countSet);

          // ÏÑ∏Ìä∏ Îãπ Ïπ¥Ïö¥Ìä∏ Ïàò reset
          countTime = 0;

          // breaktimer Í∞ÄÎèô
          if(breakTime>0) {
            var timer = breakTime;
            setTimer(timer);
            setTimeout(() => { webcam.pause() }, 500)   // ÌôîÎ©¥ Ï†ïÏßÄ
            handleOpen(); // Î™®Îã¨Ï∞Ω on

            var timerInterval = setInterval(() => {
              if(timer==0) clearInterval(timerInterval);
              timer--;
              setTimer(timer);
            }, 1000);
            setTimeout(() => { handleClose() }, breakTime*1000)       // Î™®Îã¨ close     
            setTimeout(() => { webcam.play() }, breakTime*1000)          
          }
        }


      }
      status = 'wait';
    } else if (prediction[1].probability.toFixed(2) > 0.9) {
      status = 'action';
    }

    // for (let i = 0; i < maxPredictions; i++) {
    //   const classPrediction =
    //     prediction[i].className + ': ' + prediction[i].probability.toFixed(2);
    //   labelContainer.childNodes[i].innerHTML = classPrediction;
    // }

    // finally draw the poses
    drawPose(pose);
  }

  function drawPose(pose) {
    if (webcam.canvas) {
      ctx.drawImage(webcam.canvas, 0, 0);
      // draw the keypoints and skeleton
      if (pose) {
        const minPartConfidence = 0.5;
        tmPose.drawKeypoints(pose.keypoints, minPartConfidence, ctx);
        tmPose.drawSkeleton(pose.keypoints, minPartConfidence, ctx);
      }
    }
  }

  const handleExercise = (cnt) => {
    console.log("ü§ûü§ûü§ûü§ûü§ûü§ûü§û ", cnt);
    dispatch({
      type: EXERCISE_HISTORY_REGISTER_REQUEST,
      data: {
        count: cnt,
        date: dayjs(now).format('YYYY-MM-DD HH:mm:ss'),
        exerciseNo: exercise.type,
      },
    });
  }

  return (
    <div className='myExercise'>
      { pageLoading ? 
        <>
        <div className='menuTitle'>MYÏö¥Îèô</div>
        <Row class='boxWrapper'>
          <Col md='1'></Col>
          <Col md='2'>
          <div class='imageWrapper'>
            <div class='exerciseTitle'>üèãÔ∏è‚Äç‚ôÄÔ∏èÏö¥Îèô Î∞©Î≤ï</div>
            <div>
              <img src={beforeImageURL} class='exerciseImage' alt='Ïö¥Îèô ÏãúÏûë ÎèôÏûë'/>
            </div>
            <img src={ arrowImage } class='arrowImage' />
            <div>
              <img src={afterImageURL} class='exerciseImage' alt='Ïö¥Îèô ÎÅù ÎèôÏûë'></img>
            </div>
          </div>
          </Col>
          <Col md='5'
            class='leftBox'>
            <div class='leftBox'>
              <canvas id='canvas'></canvas>
            </div>
          </Col>
          <Col md='3'>
            <div class='exerciseInfo fontBold'>
              <div class='exerciseName'>{ ExerciseTypeList[exercise.type-1] }</div>
              <div>Ï¥ù ÏÑ∏Ìä∏ : <span class='fontNormal'>{ exercise.set }</span></div>
              <div>ÏÑ∏Ìä∏Îãπ ÌöüÏàò : <span class='fontNormal'>{ exercise.time }</span></div>
            </div>
            <div class='timeInfo'>
              <div class='frame'>
                <div class='center'>
                  <div class='headline fontBold'>
                    Counter
                  </div>
                  <div class='currentSet fontNormal'>
                    { currentSet } ÏÑ∏Ìä∏
                  </div>
                  <div class='circle-big'>
                    <div class='text'>
                      <span id='counter'>0</span>
                      <div class='small'>Í∞ú</div>
                    </div>
                    <svg>
                      <circle class='bg' cx='57' cy='57' r='52' />
                      <circle class='progress' cx='57' cy='57' r='52' />
                    </svg>
                  </div>
                </div>
              </div>  
            </div>
          </Col>
          <Col md='1'></Col>
        </Row> 
        <div class='buttonWrapper'>
          <Button class='exitButton' onClick={ () => handleExercise(currentCount) }>Ï¢ÖÎ£å</Button>
        </div>

        {/* <div id='label-container'></div> */}
        <Modal
          open={open}
          onClose={handleClose}
          aria-labelledby='modal-modal-title'
          aria-describedby='modal-modal-description'
        >
          <Box sx={style}>
            <Typography 
              class='fontNormal'
              style={{ marginTop: '20px' }}
              id='modal-modal-title' 
              variant='h6' 
              component='h2'>
              Ïâ¨Îäî ÏãúÍ∞Ñ Ï¢ÖÎ£åÍπåÏßÄ
            </Typography> 
            <Typography 
              class='fontBold'
              style={{ fontSize: '70px', marginTop: '30px'}}
              id='modal-modal-description' 
              sx={{ mt: 2 }}>
              { timer } <span class='fontNormal' style={{ fontSize: '20px' }}>Ï¥à</span>
            </Typography>
          </Box>
        </Modal>
      </>
    : 
      <div class='loadingWrapper'>
        <div id='loading'></div>
      </div>
    }
    </div>
  );
}
