import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import PieChart, {
    Legend,
    Export,
    Series,
    Label,
    Font,
    Connector,
} from 'devextreme-react/pie-chart';
import { NUTRITION_HISTORY_REQUEST } from '../../../store/modules/myPage';
import {
    MessageWrapper,
    LackMessage,
    NormalMessage,
    TooMuchMessage,
    Title,
    DatePickerWrapper,
    EmptyText,
    Description,
    editBox,
    modalTitle,
    modalBody,
    CancelButton,
    ButtonWrapper,
} from '../MyPage.style';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function customizeText(arg) {
    // return `${arg.argument} ${arg.valueText} (${arg.percentText})`;
    return `${arg.valueText} (${arg.percentText})`;
}



export default function NutritionHistory() {
    const dispatch = useDispatch();
    const { nutritionHistoryList } = useSelector(state => state.mypage);
    const { me } = useSelector(state => state.mypage);
    const [ startDate, setStartDate ] = useState(new Date());

    const CarbohydrateList = [];
    const ProteinList = [];
    const FatList = [];
    const dataSource = [];

    var totalCarbohydrate = 0;
    var totalProtein = 0;
    var totalFat = 0;

    if(nutritionHistoryList.length !== 0) {
        for(let i=0; i<nutritionHistoryList.length; i++) {
            CarbohydrateList.push(nutritionHistoryList[i].carbohydrate);
            ProteinList.push(nutritionHistoryList[i].protein);
            FatList.push(nutritionHistoryList[i].fat);
        }
        totalCarbohydrate = CarbohydrateList.reduce((sum, currValue) => {
            return sum + currValue;
        });
        totalProtein = ProteinList.reduce((sum, currValue) => {
            return sum + currValue;
        });
        totalFat = FatList.reduce((sum, currValue) => {
            return sum + currValue;
        });

        dataSource.push({ nutrition: 'ÌÉÑÏàòÌôîÎ¨º', amount: totalCarbohydrate.toFixed(2) },)
        dataSource.push({ nutrition: 'Îã®Î∞±Ïßà', amount: totalProtein.toFixed(2) },)
        dataSource.push({ nutrition: 'ÏßÄÎ∞©', amount: totalFat.toFixed(2) },)
    }

    const now = new Date();   // ÌòÑÏû¨ ÎÇ†Ïßú Î∞è ÏãúÍ∞Ñ
    const year = now.getFullYear(); // Ïó∞ÎèÑ
    const month = now.getMonth();   // Ïõî
    const day = now.getDate();      // Ïùº
    var userYear = '';
    var userMonth = '';
    var userDay = '';
    if(me.birthday !== null) {
        userYear = me.birthday.substring(0, 4);
        userMonth = me.birthday.substring(5, 7);
        userDay = me.birthday.substring(9, 10);
    }

    var age = 0;

    if(month < userMonth) {
        age = year - userYear - 1;
    } else if(month === userYear) {
        if(day < userDay) {
        age = year - userYear - 1;
        }
    }

    // Ïú†Ï†Ä Í∏∞Ï¥àÎåÄÏÇ¨Îüâ Íµ¨ÌïòÍ∏∞ (Mifflin-St Jeor Equation)
    var bmr = 0;
    if(!me.gender) { // ÎÇ®ÏÑ±Ïù∏ Í≤ΩÏö∞
        bmr = (me.weight * 10 + me.height * 6.25 - 5 * age + 5);
    } else {         // Ïó¨ÏÑ±Ïù∏ Í≤ΩÏö∞
        bmr = (me.weight * 10 + me.height * 6.25 - 5 * age - 161);
    }

    // Ïù¥ÏÉÅÏ†ÅÏù∏ ÌÉÑÎã®ÏßÄ ÎπÑÏú®ÏùÄ 4:4:2 ÎòêÎäî 5:2:3 Ïù¥Îã§. 
    // Ïó¨Í∏∞ÏÑú ÏÑ≠Ï∑®ÎüâÏùÑ Í≥ÑÏÇ∞ÌïòÎ©¥,
    // ÌÉÑÏàòÌôîÎ¨º : bmr * 0.5 / 4
