    Legend,
    Export,
    Series,
    Label,
    Font,
    Connector,
} from 'devextreme-react/pie-chart';
import { NUTRITION_HISTORY_REQUEST } from '../../../store/modules/myPage';
import {
    MessageWrapper,
    LackMessage,
    NormalMessage,
    TooMuchMessage,
    Title,
    DatePickerWrapper,
    EmptyText,
    Description,
